import React, { useLayoutEffect, useState } from "react";
import { View, Text, StyleSheet, TouchableOpacity, KeyboardAvoidingView, TextInput, Platform, ScrollView, ActivityIndicator, Image } from "react-native";
import { FontAwesome5, Entypo, MaterialIcons, Ionicons } from "@expo/vector-icons";
import { useSelector, useDispatch } from "react-redux";
import { firestoreDB } from "../config/firebase";

const ProfileScreen = ({ route, navigation }) => {
    const user = useSelector((state) => state.user);
    const dispatch = useDispatch();

    const handleLogout = () => {
        // Dispatch logout action here
        // Example: dispatch(logoutAction());
    };

    const handlePrivacySettings = () => {
        // Navigate to privacy settings screen
        // Example: navigation.navigate('PrivacySettings');
    };

    return (
        <View style={styles.container}>
            <Image source={{ uri: user.profilePic }} style={styles.profilePic} />
            <View style={styles.userInfo}>
                <Text style={styles.userName}>{user.name}</Text>
                <Text style={styles.userEmail}>{user.email}</Text>
            </View>
            <TouchableOpacity style={styles.button} onPress={handlePrivacySettings}>
                <Text style={styles.buttonText}>Privacy Settings</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.button} onPress={handleLogout}>
                <Text style={styles.buttonText}>Logout</Text>
            </TouchableOpacity>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#fff',
    },
    profilePic: {
        width: 150,
        height: 150,
        borderRadius: 75,
        marginBottom: 20,
    },
    userInfo: {
        alignItems: 'center',
    },
    userName: {
        fontSize: 24,
        fontWeight: 'bold',
        marginBottom: 5,
    },
    userEmail: {
        fontSize: 18,
        color: '#555',
    },
    button: {
        backgroundColor: '#007bff',
        padding: 10,
        borderRadius: 5,
        marginTop: 10,
    },
    buttonText: {
        color: '#fff',
        fontSize: 18,
    },
});

export default ProfileScreen;
