import { MaterialIcons } from "@expo/vector-icons";
import React from "react";
import { Text,Image, View, TouchableOpacity } from "react-native";
import { useSelector } from "react-redux";
const AddToChatScreen = (props) => {
    const user = useSelector((state) => state.user);

    return (
        <View>
            <View style={{
                width: '100%',
                height:'50%',
                backgroundColor: '#36802d',
                paddingTop: 24,
                paddingBottom: 104,
                paddingLeft: 16,
                paddingRight: 16,
                flex: 0.85,
            }}>
            
            <View style={{
                flexDirection: 'row',
                alignItems: 'center',
                justifyContent: 'space-between',
                paddingTop: 24,
                paddingBottom: 24,
                paddingLeft: 12,
                paddingRight: 12,
            }}>
                <TouchableOpacity  style={{ position: 'absolute', left: 10, top: 30 }} onPress={() => props.navigation.goBack()}>
                    <MaterialIcons name="chevron-left" size={32} color={'white'} />
                </TouchableOpacity>
                <TouchableOpacity style={{ position: 'absolute', right: 20, top: 20 }} onPress={() => console.log('Profile picture pressed')}>
    <Image 
        source={{ uri: user.profilePic }} 
        resizeMode="cover"  
        style={{ width: 50, height: 50, borderRadius: 25, borderWidth: 3, borderColor: '#77AB59' }} 
    />
</TouchableOpacity>
            </View>
            </View>
            <View style={{flex:1,borderTopLeftRadius:20,borderTopRightRadius:30}}>

            </View>
        </View>
    );
}

export default AddToChatScreen;
