import { MaterialIcons } from "@expo/vector-icons";
import React from "react";
import { Image, TouchableOpacity, View } from "react-native";
import { useSelector } from "react-redux";

const AddToChatScreen = (props) => {
    const user = useSelector((state) => state.user);

    return (
        <View style={{ flex: 1 }}>
            {/* Header Section */}
            <View style={{
                backgroundColor: '#36802d',
                paddingTop: 70,
                paddingBottom: 70,
                paddingLeft: 16,
                paddingRight: 16,
                flex: 0.2, // Adjust the height of the header as needed
            }}>
                <View style={{
                    flexDirection: 'row',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                }}>
                    <TouchableOpacity style={{}} onPress={() => props.navigation.goBack()}>
                        <MaterialIcons name="chevron-left" size={32} color={'white'} />
                    </TouchableOpacity>
                    <TouchableOpacity onPress={() => console.log('Profile picture pressed')}>
                        <Image 
                            source={{ uri: user.profilePic }} 
                            resizeMode="cover"  
                            style={{ width: 50, height: 50, borderRadius: 25, borderWidth: 3, borderColor: '#77AB59' }} 
                        />
                    </TouchableOpacity>
                </View>
            </View>

            {/* Content Section */}
            <View style={{
                flex: 0.8, // Adjust the height of the content section as needed
                borderTopLeftRadius: 20,
                borderTopRightRadius: 30,
                backgroundColor: 'white', // Add background color or any other styles as needed
                justifyContent: 'center', // Adjust alignment as needed
                alignItems: 'center', // Adjust alignment as needed
            }}>
                {/* Your content goes here */}
            </View>
        </View>
    );
}

export default AddToChatScreen;
