import React, { useState } from "react";
import { ActivityIndicator, Image, ScrollView, Text, TouchableOpacity, View } from "react-native";
import { useSelector } from "react-redux";
import { FontAwesome, Ionicons } from "@expo/vector-icons";
import logo from '../../assets/logo.png';
import { SafeAreaView } from "react-native";

const HomeScreen = () => {
    const user = useSelector((state) => state.user);
    const [isLoading, setIsLoading] = useState(false);
    console.log("logged user:", user);
    
    return (
        <View>
            <SafeAreaView>
                <View style={{ flexDirection: 'row', alignItems: 'center' }}>
                    <Image 
                        source={logo} 
                        style={{ height: 50, width: 70, marginTop: 40, marginLeft: 20 }} 
                        resizeMode="contain"
                        accessibilityLabel="Logo" 
                    />
                    <TouchableOpacity style={{ marginLeft: 20 }} onPress={() => console.log('Profile picture pressed')}>
                        <Image 
                            source={{ uri: user.profilePic }} 
                            resizeMode="cover"  
                            style={{ marginLeft: 160, marginTop: 30, width: 50, height: 50, borderRadius: 25, borderWidth: 3, borderColor: '#77AB59' }} 
                        />
                    </TouchableOpacity>
                </View>
                <ScrollView>
                    <View>
                        <View style={{ flexDirection: 'row', alignItems: 'center', marginTop: 20, marginLeft: 20 }}>
                            <Text style={{ fontWeight: "bold", fontSize: 20 }}>Messages</Text>
                            <TouchableOpacity style={{ marginLeft: 170 }}>
                                <Ionicons name="chatbox" size={28} color="#555" />
                            </TouchableOpacity>
                        </View>
                        {/* Conditional rendering of loading indicator */}
                        {isLoading ? (
                            <View style={{ alignSelf: 'center', marginTop: 20 }}>
                                <ActivityIndicator size="large" color="#43C651" />
                            </View>
                        ) : (
                            <MessageCard />
                        )}
                    </View>
                </ScrollView>
            </SafeAreaView>
        </View>
    );
};

const MessageCard = () => {
    return (
        <TouchableOpacity style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'flex-start', padding: 10 }}>
            {/* image of user */}
            <View style={{ width: 60, height: 60, borderRadius: 5, alignItems: 'center', justifyContent: 'center', borderWidth: 3, borderColor: 'green' }}>
                <FontAwesome name="users" size={24} color="#555" />
            </View>
            {/* content */}
            <View style={{ marginLeft: 10 }}>
                <Text style={{ fontWeight: 'bold', marginBottom: 5 }}>Messages Title</Text>
                <Text>Lorem is the message that is displaying. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed ullamcorper tortor vitae ipsum consectetur, et finibus arcu sollicitudin.</Text>
            </View>
        </TouchableOpacity>
    );
};

export default MessageCard;
