import { View, Text,StyleSheet,Image,TouchableOpacity } from 'react-native'
import React, { useState } from 'react'
import Input from '../componets/Input'
import Buttons from '../componets/Buttons';
import { avatars } from '../Supports/avatar';
import pic from '../../assets/flower.jpg';
import logo from '../../assets/logo.png'
const Login = (props) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isEmailValid, setIsEmailValid] = useState(false);
   const [avatar, setAvatar] = useState(avatars[0].image.asset.url);
   useEffect(()=>{
    if(email.includes('@')&&email.includes('.com'))
    {
      setIsEmailValid(true)
    }
    else
    setIsEmailValid(false)
  if(password.length>6||password.length==6)
  setIsPasswordValid(true)
else
setIsPasswordValid(false)
  },[email,password])
    return (
        <View style={styles.container}>
            
            <Image source={pic} style={styles.backgroundImage} resizeMode="cover" />
            
            <View style={styles.maincomponent}>
            <Image source={logo} style={styles.logo} resizeMode='contain'/>
                <TouchableOpacity style={{ alignItems: "center" }}>
                 <Image source={{ uri: avatar }} style={styles.avatarImage} resizeMode="contain" />
                </TouchableOpacity>
                <Text style={styles.title}>Welcome back!!</Text>
            <Input placeholder="Enter Email" value={email} onChangeText={(text) => setEmail(text)} />
    
            <Input placeholder="Enter Password" value={password} onChangeText={(text) => setPassword(text)} />
            <Buttons title='Login' />
            <View style={styles.loginContainer}>
                <Text style={styles.loginText}>Does't have an account??</Text>
                <TouchableOpacity onPress={() => props.navigation.navigate('Signup')}>
                    <Text style={styles.loginLink}> Sign up</Text>
                </TouchableOpacity>
            </View>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'white',
    },
    maincomponent:{
        borderTopLeftRadius:110,
        backgroundColor:'white',
marginTop:'40%'
    },logo:{
        alignSelf:'center',
        height:'15%'
    },
    title: {
        fontSize: 20,
        color: 'black',
        alignSelf: 'center',
    },
    backgroundImage: {
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        height:'50%'
    },
    avatarImage: {
        width: 50,
        height: 50,
    },
    loginContainer: {
        flexDirection: 'row',
        marginTop: 30,
        marginLeft: '25%',
        fontSize: 15,
    },
    loginText: {
        color: 'black',
    },
    loginLink: {
        color: '#36802D',
    }
});

export default Login;
